import React from 'react';
import {
    StyleSheet,
    SafeAreaView,
    View,
    Text,
    Image,
    TouchableOpacity,
    FlatList,
    Modal
} from 'react-native'

import {
    InnerContainer,
    PageTitle,
    Subtitle,
    StyledFormArea,
    StyledButton,
    ButtonText,
    Line,
    WelcomeContainer,
    WelcomeImage,
    Avatar,
    MyTextInput
} from '../components/styles'

import { BlurView } from "expo-blur";

import { images, icons, COLORS, FONTS, SIZES } from '../constants';
import { Formik } from 'formik';

const Profile = ({ navigation }) => {

    const [showAddToBagModal, setShowAddToBagModal] = React.useState(false)
    const [selectedItem, setSelectedItem] = React.useState(null)
    const [selectedSize, setSelectedSize] = React.useState("")
    const [selectedPayMethod, setSelectedPayMethod] = React.useState("")

    // Dummy Data
    const [trending, setTrending] = React.useState([
        {
            id: 0,
            name: "Hacking with Python",
            img: images.hackingwithPython,
            bgColor: "#BF012C",
            type: "Networking",
            price: "$186",
            sizes: ["ePub", "Kindle", "Mobi", "PDF"],
            paymethod: ["Visa", "MC", "PayPal", "Debit"]
        },
        {
            id: 1,
            name: "Ace Computer Science",
            img: images.aceComputerScienceandCoding,
            bgColor: "#D39C67",
            type: "Coding",
            price: "$11.99",
            sizes: ["ePub", "Kindle", "Mobi", "PDF"],
            paymethod: ["Visa", "MC", "PayPal", "Debit"]
        },
        {
            id: 2,
            name: "Blood, Sweat, and Pixels",
            img: images.bloodSweatPixels,
            bgColor: "#7052A0",
            type: "Game Design",
            price: "$21.77",
            sizes: ["ePub", "Kindle", "Mobi", "PDF"],
            paymethod: ["Visa", "MC", "PayPal", "Debit"]
        },
    ]);

    const [recentlyViewed, setRecentlyViewed] = React.useState([
        {
            id: 0,
            name: "Master Card",
            img: images.master_card,
            bgColor: "#414045",
            type: "Credit",
            price: "$71.70",
            sizes: ["ePub", "Kindle", "Mobi", "PDF"],
            paymethod: ["Visa", "MC", "PayPal", "Debit"]
        },
        {
            id: 1,
            name: "Visa",
            img: images.visa,
            bgColor: "#4EABA6",
            type: "Credit",
            price: "$41.06",
            sizes: ["ePub", "Kindle", "Mobi", "PDF"],
            paymethod: ["Visa", "MC", "PayPal", "Debit"]
        },
        {
            id: 2,
            name: "PayPal",
            img: images.paypal,
